export function MailNotes() {
  const { user } = useContext(UserContext)
  const [isPencil, setIsPencil] = useState(false)
  const [agents, setAgents] = useState([
    {name: 'Иванов А. П.', colored: false},
    {name: 'Преображенский Н. А.', colored: false},
    {name: 'Махайлов И. С.', colored: false},
    {name: 'Васильев А. В.', colored: false},
    {name: 'Кузнецов В. А.', colored: false},
    {name: 'Одоевский А. А.', colored: false},
    {name: 'Смирнов Д. И.', colored: false},
    {name: 'Новиков Е. А.', colored: false},
  ])

  useEffect(() => {
    setAgents(agents => agents.map(((agent, key) => {return { name: agent.name, colored: !!user?.colored_agents.includes(key) }})))
  }, [user, setAgents])

  function toggleColored(index: number) {
    if (!isPencil)
      return

    axios.post(`${BACKEND_IP}/api/toggle_agent_color`, {
      agent: index,
      colored: !agents[index].colored,
      token: localStorage.getItem('token')
    })
    setAgents(agents.with(index, { name: agents[index].name, colored: !agents[index].colored }))
  }

  function togglePencil() {
    setIsPencil(!isPencil)
  }
  if ((user?.current_level ?? 0) <= 5) return <></>
  return (
    <NotesBaseForm>
      <div className={"text-6xl"} style={{ gridArea: `5 / 10 / span 2 / span 30` }}>Список агентов:</div>
      <button onClick={togglePencil} type={"button"} style={{ gridArea: `20 / 4 / span 2 / span 10`, backgroundImage: `url(${PENCIL})`, backgroundPosition: "center", width: "100px", height: "100px", border: isPencil ? "red solid 4px" : "" }}></button>
      {agents.map((agent, key) =>
        <div onClick={() => toggleColored(key)} className={"text-4xl select-none"} style={{ cursor: isPencil ? "pointer" : "default", textDecoration: agents[key].colored ? "line-through red" : "", gridArea: `${8+(key*2)} / 12 / span 2 / span 30` }} key={key}>{key+1}. {agent.name}</div>)}
    </NotesBaseForm>
  )
}
